INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'入侵检测系统的组成及特点', N'入侵检测系统组成通常IDS由传感器（Sensor）与控制台（Console）两部分组成。传感器主要包括事件发生器、事件分析器和事件数据库等，负责采集数据（网络包、系统日志等）、分析数据并生成安全事件。 控制台包括发现入侵、响应单元和作出相应等部件/模块，主要起到中央管理的作用，商品化的IDS通常提供图形界面的控制台。 入侵检测系统的特点入侵检测技术是动态安全技术的最核心技术之一。入侵检测被认为是防火墙之后的第二道安全闸门，提供对内部攻击、外部攻击和误操作的实时保护', N'黑客与入侵检测', N'170', N'入侵 检测 事件 系统 控制台 安全 包括 技术 主要 攻击 ids 组成 ', null, null, null)
