INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'为什么要进行系统测评', N'（1）	政策要求信息安全等级保护管理办法（公通字[2007]43号)第十四条：信息系统建设完成后，运营、使用单位或者其主管部门应当选择符合本办法规定条件的测评机构，依据《信息系统安全等级保护测评要求》等技术标准，定期对信息系统安全等级状况开展等级测评。中央财政资金电子政务建设项目建设单位向审批部门提出项目竣工验收申请时，要提供备案证明、测评报告风、险评估报告。(发改高技[2008]:2071号)（2）	内部需求1)	确定当前安全保护能力水平。2)	找出差距，为后续工作提供依据。', N'信息系统测评', N'171', N'测评 等级 信息 保护 提供 报告 要求 安全 系统安全 依据 使用 机构 ', null, null, null)
