INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'等级保护基本要求的核心', N'等级保护基本要求的核心是安全保护能力，即需要达到的基本安全状态。等级保护测评的重点就是信息系统的安全保护能力。安全保护能力可分为恢复能力和对抗能力。', N'信息系统测评', N'173', N'保护 能力 安全 等级 基本要求 达到 状态 可分为 核心 恢复能力 需要 测评 ', null, null, null)
