INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'安全保护能力要求', N'（1）	第一级安全保护能力(自主)经过安全建设整改，信息系统能够抵御一般性的攻击，具有防范常见的计算机病毒和恶意代码危害的能力；系统遭到破坏后，还可以恢复系统的主要功能。（2）	第二级安全保护能力（指导）经过安全建设整改，信息系统具有抵御小规模、强度较弱的恶意攻击的能力，防范一般性计算机病毒和恶意代码危害的能力，抵御一般的自然灾害的能力，检测常见的攻击行为并对安全事件进行记录的能力。系统遭到破坏后，还可以恢复系统的正常运行状态。（3）	第三级安全保护能力（监督）经过安全建设整改，信息系统在统一的安全保护策略下具有抵御大规模、较强恶意攻击的能力，防范计算机病毒和恶意代码危害的能力，抵御较为严重的自然灾害的能力，对系统资源、用户、安全机制等进行集中控管的能力，检测、发现、报警、记录入侵行为的能力，对安全事件进行响应处置并能够追踪安全责任的能力。在系统遭到破坏后，还可以较快地恢复正常地运行状态。对于服务保障性要求高的系统，能够快速恢复正常的运行状态。', N'信息系统测评', N'174', N'能力 安全 系统 抵御 恢复 保护 防范 状态 建设 破坏 攻击 正常 ', null, null, null)
