INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'等级保护测评内容', N'单元测评：包括测评指标（依照基本要求）、测评实施（描述测评过程中使用的具体测评方法、涉及的测评对象）、结果判定（分为符合、不符合、部分符合、不适用）。整体测评：是在单元测评的基础上，通过进一步分析信息系统的整体安全性，对信息系统实施的综合安全测评', N'信息系统测评', N'175', N'测评 单元 整体 实施 符合 信息系统 分析 基本要求 使用 对象 综合 包括 ', null, null, null)
