INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'等级保护测评方法', N'基本测评方法有以下三种：（1）	访谈（Interview）：是通过引导信息系统相关人员进行有目的的或有针对性的交流以帮助测评人员理解、分析或取得证据的过程，从而了解信息系统的全局性。（2）	检查（Examine）：对文档、机制、设备等进行检查，包括评审、核查、审查、观察、研究和分析等方式  。（3）	测试（Test）：借助特定工具，如扫描检测工具、攻击工具、渗透工具、网络协议分析仪等，对机制和设备进行测试，包括功能/性能测试、渗透测试等', N'信息系统测评', N'176', N'测试 工具 包括 设备 机制 信息系统 进行 分析 测评 渗透 人员 取得 ', null, null, null)
