INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'计算机的类型', N'计算机是一种能够按照事先存储的程序，自动、高速地进行大量数值计算和各种信息处理的现代化智能电子设备，计算机传统上分为三类:（1）	大型主机：一般为高性能的并行处理系统', N'计算机基础', N'178', N'计算机 进行 数值 大量 各种 一种 计算 能够 智能 大型 事先 高速 ', null, null, null)
