INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'软件', N'软件（中国大陆及香港用语，台湾称作软体，英文：Software）是一系列按照特定顺序组织的计算机数据和指令的集合。国家标准中对软件的定义为：与计算机系统操作有关的计算机程序、规程、规则，以及可能有的文件、文档及数据。简单来说软件就是程序加文档的集合体。一般来讲软件被划分为系统软件、应用软件和介于这两者之间的中间件。软件是用户与硬件之间的接口界面。用户主要是通过软件与计算机进行交流。', N'计算机基础', N'180', N'软件 计算机 用户 之间 文档 程序 数据 台湾 software 硬件 有关 组织 ', null, null, null)
