INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'软件开发', N'软件开发是根据用户要求建造出软件系统或者系统中的软件部分的过程，它包括需求捕捉、需求分析、设计、实现和测试。需求捕捉是与用户沟通获取用户的需求信息的过程；需求分析是对用户的需求信息进行过滤，去粗取精，去伪取真，准确找出用户的真正需求，然后把它用需求规格说明书表达出来的过程。设计分为概要设计和详细设计两个阶段，概要设计的主要任务是给出软件的模块结构，用软件结构图表示；详细设计的主要任务是设计模块的程序流程、算法、数据结构及数据库设计。实现是把设计的模块转换成计算机可以接受的程序，即以源代码实现功能。测试是在规定的条件下对程序进行操作，以发现程序错误和漏洞，并对其是否能满足设计要求进行评估的过程。简单来说，软件开发就是一系列最终构建出软件产品的活动。', N'计算机基础', N'181', N'设计 需求 用户 过程 程序 软件 进行 模块 捕捉 软件开发 信息 详细 ', null, null, null)
