INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'程序设计语言', N'程序设计语言是用于书写计算机程序而人为设计的符号语言，用于对计算进行描述、组织和推导。语言的基础是一组记号和一组规则。根据规则将记号构成的记号串的集合就是语言。在程序设计语言中，这些记号串就是程序。程序设计语言为程序员写出一个好的程序提供了所需的抽象机制、组织原则以及控制结构。常用的程序设计语言有C语言、C++、Java、Python、PHP、VB等，根据其特点不同使用情况也不同，例如，PHP专门用来显示网页，C语言被广泛用于操作系统和编译器的开发。', N'计算机基础', N'182', N'语言 记号 程序设计语言 用于 程序 就是 不同 一组 根据 php 构成 c++ ', null, null, null)
