INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'算法', N'算法（Algorithm）是指解决方案的准确而完整的描述，在计算机中表现为指令的有序序列，并且每条指令表示一个或多个操作。算法代表着用系统的方法描述解决问题的策略机制。简单来说，算法就是为了解决某个问题，而制定了一系列步骤。', N'计算机基础', N'183', N'算法 描述 指令 策略 表示 现为 为了 一系列 系统 中表 步骤 并且 ', null, null, null)
