INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'数据结构的逻辑结构', N'数据的逻辑结构指的是反映数据元素之间的逻辑关系的数据结构，逻辑结构包括：集合结构：数据结构中的元素之间除了“同属一个集合” 的相互关系外，别无其他关系，即集合结构里面的元素关系是孤立的；线性结构：数据结构中的元素存在一对一的相互关系；树形结构：数据结构中的元素存在一对多的相互关系； 图形结构：数据结构中的元素存在多对多的相互关系。', N'计算机基础', N'184', N'结构 元素 数据结构 相互关系 集合 存在 之间 关系 逻辑 数据 里面 线性 ', null, null, null)
