INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'数据结构的存储结构', N'数据的存储结构指的是数据的逻辑结构在计算机中的存储形式。存储结构有两种：顺序存储结构：把数据元素存放在地址连续的存储单元里，其数据间的逻辑关系和物理关系是一致的。链式存储结构：把数据元素存放在任意的存储单元里，这组存储单元可以是连续的，也可以是不连续的。第十三章 	操作系统', N'计算机基础', N'185', N'数据 存储 结构 存储单元 连续 存放在 以是 元素 两种 关系 链式 逻辑 ', null, null, null)
