INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'操作系统', N'操作系统（Operating System，缩写：OS）是管理计算机硬件与软件资源的计算机程序，为计算机程序提供公共服务。操作系统需要处理各种事务，如决定系统资源供需的优先次序、控制输入与输出设备、操作网络与管理文件系统、管理与配置内存等基本事务。操作系统提供一个让用户与系统交互的操作界面。', N'计算机基础', N'186', N'管理 操作系统 提供 系统 操作 程序 计算机 事务 用户 各种 次序 文件 ', null, null, null)
