INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'语言处理程序', N'语言处理程序是为用户设计的编程服务软件，其作用是将高级语言源程序翻译成计算机能识别的目标程序，以便计算机能够运行。语言处理程序一般是由汇编程序、编译程序、解释程序和相应的操作程序等组成。汇编程序的任务是将汇编语言编写的源程序翻译成机器指令程序。编译程序（也称编译器）的任务是将源程序编译成目标语言程序。解释程序（也称解释器）有两种，一种是直接解释执行源程序的解释程序，另一种是将源程序翻译成某种中间代码再执行的解释程序。', N'计算机基础', N'187', N'源程序 程序 解释程序 语言 翻译成 处理 执行 任务 解释 汇编程序 编译程序 用户 ', null, null, null)
