INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'Windows操作系统有哪些特点？', N'（1）	直观、高效的图形用户界面：Windows采用了图形化模式GUI，操作简单，容易使用。（2）	用户界面统一：Windows应用程序大多符合IBM公司提出的CUA (Common User Acess)标准，所有的程序拥有相同或相似的外观，用户学会了一个软件的操作使用，就很容易学会其他软件。丰富的设备无关的图形操作：提供丰富的图形操作函数，可以绘制出各种几何图形，如线、圆，矩形等，支持各种输出设备，并且在不同设备上可以显示相同的效果。多任务：允许用户同时运行多个应用程序，或在一个程序中同时做几件事情。', N'操作系统', N'192', N'用户 使用 丰富 界面 设备 相同 操作 一个 图形操作 应用程序 各种 可以 ', null, null, null)
GO
