INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'macOS操作系统的介绍', N'macOS 是一套由苹果公司自行开发的运行于苹果 Macintosh 系列电脑上的操作系统。Mac系统是基于Unix内核的图形化操作系统，简单易用且功能强大，一般情况下在普通PC上无法安装。现行的最新的系统版本是macOS 10.14 ，且网上也有在PC上运行的Mac系统，简称 Mac PC。另外，疯狂肆虐的电脑病毒几乎都是针对Windows的，由于MAC的架构与Windows不同，而且用户比较少，所以很少受到病毒的袭击。macOS可以被分成操作系统的两个系列：（1）老旧且已不被支持的“Classic” mcOS，它是一个100%的图形操作系统，完全没有命令行模式。但是它几乎没有内存管理、协同式多任务和对扩展冲突敏感。（2）新的macOSX，它结合BSD Unix、OpenStep和Mac OS 9的元素，它以Unix为基础，其代码被称为Darwin，实行的是部分开放源代码。', N'操作系统', N'195', N'mac 操作系统 unix 系统 macos pc windows 运行 系列 扩展 用户 架构 ', null, null, null)
