INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'3W服务（WWW）', N'WWW(World Wide Web)服务是一种建立在超文本基础上浏览、查询因特网信息的方式。它以交互方式查询并访问存放于远程计算机的信息，其核心技术为超文本传输协议和超文本标记语言。由于www服务使用的是超文本链接（HTML），可以很方便的从一个信息页转换到另一个信息页。它不仅能查看文字，还可以欣赏图片、音乐、动画，为用户获取信息资源提供了便利条件。', N'操作系统', N'197', N'超文本 信息 查询 www 可以 服务 方式 欣赏 用户 换到 使用 图片 ', null, null, null)
