INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'macOS操作系统有哪些特点', N'全屏模式：一切应用程序均可以在全屏模式下运行，减少多个窗口带来的困扰。（1）	任务控制：整合了Dock和控制面板，并可以窗口和全屏模式查看各种应用。（2）	快速启动面板：其工作方式与iPad完全相同，它以类似于iPad的用户界面显示电脑中安装的一切应用，并通过App Store进行管理。（3）	应用商店：当用户从应用商店购买一个应用后，Mac电脑会自动将它安装到快速启动面板中，使用户无需对应用进行管理。', N'操作系统', N'198', N'应用 用户 模式 全屏 进行 管理 一切 面板 启动 快速 商店 窗口 ', null, null, null)
