INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'Office办公软件的简单介绍', N'Microsoft Office是一套由微软公司开发的办公软件套装，它为 Microsoft Windows 和 Mac OS X而开发，常用组件有 Word、Excel、Power point等。最新版本为Office 365(Office 2019)。该软件最初出现于九十年代早期，最初是一个推广名称，指一些以前曾单独发售的软件的合集。当时主要的推广重点是购买合集比单独购买要省很多钱。最初的 Office 版本只有 Word、Excel 和 Power point；另外一个专业版包含 Microsoft Access；经过不断发展，Office 应用程序逐渐整合，共享一些特性，例如拼写和语法检查、OLE 数据整合和微软 Microsoft VBA（Visual Basicfor Applications）脚本语言。', N'操作系统', N'199', N'office microsoft 最初 一些 整合 一个 合集 word 单独 point 开发 power ', null, null, null)
