INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'什么是WPS？', N'WPS Office是由金山软件股份有限公司自主研发的一款办公软件套装，可以实现办公软件最常用的文字、表格、演示等多种功能。具有内存占用低、运行速度快、体积小巧、强大插件平台支持、免费提供海量在线存储空间及文档模板。', N'操作系统', N'200', N'软件 办公 自主 占用 表格 套装 免费 提供 存储空间 office 速度快 金山 ', null, null, null)
