INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'Windows操作系统的常用快捷方式', N'基础快捷键：（6）	Ctrl+C ( Ctrl+Insert)：复制选中项目（7）	Ctrl+X：剪切选中项目（8）	Ctrl+V ( Shift+Insert)：粘贴选中项目（9）	Ctrl+Z：撤销（10）	Ctrl+Y：重做（11）	Delete ( Ctrl+D)：删除选中项目至回收站（12）	Shift+Delete：直接删除选中项目（13）	Ctrl+A：全选Win键相关的快捷键：（1）	Windows徽标：“开始”菜单（2）	Windows徽标+D：显示桌面（3）	Windows徽标+M：最小化窗口（4）	Windows徽标+E：开启资源管理器（5）	Windows徽标+F：查找文件或文件夹，查找文件目录，在硬盘内查找文件（6）	Windows徽标+F1：启动系统帮助程序（7）	Windows徽标+R：启动运行对话框？（8）	SHIFT+Windows 徽标+M：撤消全部最小化（9）	CTRL+Windows 徽标+F：查找计算机（10）	CTRL+Windows 徽标+TAB：将焦点从“开始”菜单移动到“快速启动”工具栏或系统任务栏（使用右箭头键或左箭头键可将焦点移动到“快速启动”工具栏和系统任务栏中的项目）（11）	Windows 徽标+TAB：在任务栏按钮之间循环（12）	Windows 徽标+Break：“系统属性”对话框', N'操作系统', N'202', N'徽标 windows 项目 选中 系统 启动 查找文件 任务栏 动到 tab 快速 10 ', null, null, null)
