INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'什么是数据库？', N'数据库（Database）是以一定方式储存在一起、能与多个用户共享、具有尽可能小的冗余度、与应用程序彼此独立的数据集合。简单来说，数据库是按照数据结构来组织、存储和管理数据的仓库。形象化来说，数据库可以看做电子化的文件柜，是存储电子文件的处所，用户可以对文件中的数据进行新增、截取、更新、删除等操作。', N'数据库', N'204', N'数据库 数据 用户 文件 存储 可以 来说 形象化 进行 管理 组织 在一起 ', null, null, null)
GO
