INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'神通数据库', N'神通数据库是一款计算机数据库，分为标准版、企业版、安全版。神通数据库标准版提供了大型关系型数据库通用的功能，功能适用，稳定可靠，性价比较高，可以满足中小型企业的总体需求。神通数据库企业版是天津神舟通用数据技术有限公司拥有自主知识产权的企业级、大型通用关系型数据库管理系统', N'数据库', N'209', N'数据库 通用 神通 大型 标准版 企业版 关系 功能 提供 稳定 拥有 管理 ', null, null, null)
GO
