INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'计算机网络拓扑结构', N'计算机网络的拓扑结构，是指将计算机组成的网络之间设备的分布情况以及连接状态以图的形式表现出来，这个图称为拓扑图。将计算机、设备及传输媒介形成的结点与线的物理连接构成模式。该结点包括两类：转接节点，用来转换和交换信息，包括交换机、集线器、终端控制器等；访问节点，包括主机、终端等。线代表各种传输媒介，包括有线和无线形式。', N'数据库', N'210', N'包括 结点 算机 将计 连接 设备 形式 节点 终端 媒介 传输 构成 ', null, null, null)
