INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'计算机网络性能指标', N'（1）	速率（网速）：主机在数字通信上传送数据的速率，其单位为比特（bit）。（2）	带宽：在计算机网络中，带宽指网络的通信线路传送数据的能力，即单位时间内从网络中的某一个点到另外一个点所能通过的“最高数据率”', N'数据库', N'211', N'数据 网络 单位 速率 带宽 网速 一个 比特 通信线路 数字通信 传送 bit ', null, null, null)
