INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'什么是计算机网络？', N'计算机网络（computer networks）也称计算机通信网，广义上来说，它是利用通信线路将地理位置不同的、具有独立功能的计算机系统和通信设备按不同的形式连接起来，以功能完善的网络软件及协议实现资源共享与信息传递的系统。最简单的计算机网络就只有两台计算机和连接它们的一条链路。从需求来说，计算机网络是由大量独立的、但相互连接的计算机来共同完成计算机任务的网络。简单来说，计算机网络就是一些相互连接的、以共享资源为目的的、自治的计算机的集合。', N'计算机网络', N'217', N'计算机 计算机网络 来说 系统 独立 不同 简单 相互连接 功能 自治 它们 大量 ', null, null, null)
GO
