INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'计算机网络层次的划分', N'计算机网络的体系结构是网络协议的层次划分与各层协议的集合，同一层中的协议根据该层所要实现的功能来确定。各对等层之间的协议功能由相应的底层提供服务完成。为了使不同的计算机厂家生产的计算机能够相互通信，以便在更大的范围内建立计算机网络，国际标准化组织（ISO）在1978年提出了“开放系统互联参考”模型（Open System Interconnection/Reference Model），即著名的OSI/RM模型。它将计算机网络体系结构的通信协议划分为七层，自下而上依次为：物理层（Physics Layer）、数据链路层（Data Link Layer）、网络层（Network Layer）、运输层（Transport Layer）、会话层（Session Layer）、表示层（Presentation Layer）、应用层（Application Layer）。除了标准的OSI七层模型以外，常见的网络层次划分还有TCP/IP的四层协议以及TCP/IP的五层协议。对于TCP/IP的四层模型，其自下而上依次为：网络接口层（对应OSI/RM模型中的物理层、数据链路层）、网络层、传输层、应用层（对应OSI/RM模型中的会话层、表示层、应用层）。对于TCP/IP的五层模型，其自下而上依次为：物理层、数据链路层、网络层、传输层、应用层（对应OSI/RM模型中的会话层、表示层、应用层）。', N'计算机网络', N'218', N'模型 layer 网络 协议 osi 应用层 ip rm tcp 物理层 会话层 依次为 ', null, null, null)
GO
