INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'文件传输（FTP）', N'文件传输协议（File Transfer Protocol，缩写：FTP）是用于在网络上进行文件传输的一套标准协议。它使用客户端/服务器（C/S）模式，能操作任何类型的文件而不需要进一步处理。但是，FTP的延时很高，也就是说，从开始请求到第一次接收需求数据之间的时间会很长，而且用户需要不时地执行一些冗长的登录过程。', N'计算机网络', N'219', N'文件 ftp 协议 传输 开始 不时 用户 冗长 使用 模式 之间 而且 ', null, null, null)
