INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'DNS协议', N'DNS是域名系统(Domain Name System)的缩写，该系统用于命名组织到域层次结构中的计算机和网络服务。域名是由圆点分开一串单词或缩写组成的，在互联网上域名与IP地址之间具有一一对应关系，例如，百度的IP地址为14.215.177.38，其域名为www.baidu.com，DNS就是进行域名解析的服务器。DNS是互联网的一项核心服务，它可以将域名和IP地址之间进行相互映射。', N'计算机网络', N'226', N'域名 地址 ip dns 进行 之间 系统 缩写 组成 名为 核心 互联 ', null, null, null)
