INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'DHCP协议', N'动态主机设置协议（Dynamic Host Configuration Protocol，缩写：DHCP）是一个局域网的网络协议，使用UDP协议工作。该协议用于内部网或网络服务供应商自动分配IP地址以及给用户或内部网络管理员作为对所有计算机进行中央管理。DHCP协议通常被应用在大型的局域网络环境中，主要用于集中管理、分配IP地址，使网络环境中的主机可以动态地获得IP地址、网关地址、DNS服务器地址等信息，能够提升地址的使用率。第四篇	国家信息化政策规划篇', N'计算机网络', N'227', N'地址 协议 ip 管理 分配 网络 环境 主机 dhcp 用于 用户 使用 ', null, null, null)
