INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N' 我国信息安全法律法规简介', N'信息安全法是指维护信息安全，狭义上是指预防信息犯罪的刑事法律规范的总称。广义上的“信息安全法的调整范围应当包括网络信息安全应急保障关系、信息共享分析和预警关系、政府机构信息安全管理、通信运营机构的安全监管、ISP的安全监管、ICP(含大型商业机构)的安全监管、家庭用户及商业企业用户的安全责任、网络与信息安全技术进出口监管、网络与信息安全标准和指南以及评估监管、网络与信息安全研究规划、网络与信息安全培训管理、网络与信息安全监控等十二个方面”。', N'信息安全相关的法律法规', N'236', N'信息 安全 网络 安全监管 用户 管理 安全法 关系 监管 机构 isp 以及 ', null, null, null)
